dict.string:
#!/usr/bin/env bash [[ -f "${TM_SUPPORT_PATH}/lib/bash_init.sh" ]] && . "${TM_SUPPORT_PATH}/lib/bash_init.sh"
labels=`"${TM_P4:=p4}" labels | cut -d " " -f 2` #echo $labels
difflabels=`osascript<<END set AppleScript's text item delimiters to {"\n", "\r"} tell app "SystemUIServer" to activate tell app "SystemUIServer" to choose from list (every text item of "$labels") with prompt "Please choose two labels:" with multiple selections allowed END` #echo $difflabels
IFS=", " difflabels=( $difflabels )
#echo "0:" ${difflabels[0]} #echo "1:" ${difflabels[1]}
/System/Library/Frameworks/Ruby.framework/Versions/1.8/usr/bin/ruby -I "$TM_BUNDLE_SUPPORT/lib/"<<END require 'p4_diff' Perforce::diff_files_in_revisions("@${difflabels[0]}", "@${difflabels[1]}", "Perforce—Diff Labels…") END
